Here is a Solidity smart contract that implements the desired logic. Please note that the contract has not been compiled or tested yet.

```solidity
pragma solidity ^0.8.0;

contract MyContract {
    mapping(address => uint256) public checkinvestments;
    mapping(address => uint256) private balanceOf;
    mapping(address => uint256) public checkwithdrawals;
    mapping(address => uint256) public checkreferral;
    uint256 public minimum = 0x2386f26fc10000;
    uint256 public step = 4;
    address public ownerwallet;
    address public owner;
    address public bountymanager;

    constructor() payable {
        require(msg.value >= minimum, "Insufficient value sent");
        ownerwallet = msg.sender;
        owner = msg.sender;
    }

    function transferOwnership(address _newOwner, address _newOwnerWallet) public {
        require(msg.sender == owner, "Only the owner can transfer ownership");
        require(_newOwner != address(0), "New owner cannot be zero address");
        owner = _newOwner;
        ownerwallet = _newOwnerWallet;
    }

    function updateReferral(address _referrer, uint256 _value) public {
        require(msg.sender == bountymanager, "Only the bounty manager can update referrals");
        checkreferral[_referrer] += _value;
    }

    function bounty() public {
        require(checkreferral[msg.sender] >= minimum && checkreferral[msg.sender] <= address(this).balance, "Invalid referral conditions");
        uint256 amount = checkreferral[msg.sender];
        checkreferral[msg.sender] = 0;
        payable(msg.sender).transfer(amount);
    }

    function withdraw() public {
        require(balanceOf[msg.sender] > 0, "No balance to withdraw");
        require(checkwithdrawals[msg.sender] < address(this).balance, "Insufficient contract balance");
        uint256 amount = checkwithdrawals[msg.sender];
        checkwithdrawals[msg.sender] = 0;
        payable(msg.sender).transfer(amount);
    }

    fallback() external payable {
        require(msg.value >= minimum, "Insufficient value sent");
        checkinvestments[msg.sender] += msg.value;
        balanceOf[msg.sender] = block.timestamp;
        if (msg.value / 100 == 0) {
            payable(ownerwallet).transfer(0);
        } else {
            payable(ownerwallet).transfer((5 * msg.value) / 100);
        }
    }
}
```

