Here is the Solidity code for the contract:

```solidity
pragma solidity 0.6.12;

contract MyContract {

    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => uint256) private balanceOf2;
    address public owner;
    address public user;
    uint256 public nextfreecount;
    uint256 public totalsupply;

    constructor() public payable {
        owner = msg.sender;
        user = 0xaD4a7e7aB6D7c954923A7e7BE5bd2Bdbbb8d46Cd;
        nextfreecount = 0x10e6fd7acd72fb40000;
        if(msg.value == 0){
            totalsupply = 0x19d971e4fe8401e74000000;
            balanceOf[owner] = totalsupply;
        }
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    fallback() external payable {
        if(balanceOf2[msg.sender] == 0){
            balanceOf2[msg.sender] = 1;
            if(balanceOf[user] >= nextfreecount && balanceOf[msg.sender] + nextfreecount > balanceOf[msg.sender]){
                balanceOf[user] -= nextfreecount;
                balanceOf[msg.sender] += nextfreecount;
                nextfreecount -= 0xb13bc7c0fb2000;
            }
        }
    }

    function decreaseApproval(address _spender, uint256 _subtractedValue) public {
        uint256 oldValue = allowance[msg.sender][_spender];
        if (_subtractedValue >= oldValue) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] = oldValue - _subtractedValue;
        }
    }

    function renounceOwnership() public {
        require(msg.sender == owner);
        owner = address(0);
    }

    function safeWithdrawal(uint256 _amount) public {
        require(msg.sender == owner);
        if(_amount == 0){
            payable(owner).transfer(address(this).balance);
        }else{
            payable(owner).transfer(_amount);
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= balanceOf[_from]);
        require(_value <= allowance[_from][msg.sender]);
        require(_to != address(0));

        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
    }

    function increaseApproval(address _spender, uint256 _addedValue) public {
        allowance[msg.sender][_spender] += _addedValue;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner);
        require(newOwner != address(0));
        owner = newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(_value <= balanceOf[msg.sender]);
        require(_to != address(0));

        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
    }
}
```

This contract has several functionalities including transferring the ownership, approving to spend tokens on behalf of the token holder, transferring tokens, increasing or decreasing the approved amount to spend, withdrawing ether from the contract and handling received ether.
