Here is a Solidity contract that implements your requirements. 

```solidity
pragma solidity >=0.4.22 <0.7.0;

contract TradeContract {
    address public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;

    mapping (address => mapping (address => uint256)) public balanceOf;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderFills;

    function order(address _tokenGet, uint256 _amountGet, address _tokenGive, uint256 _amountGive, uint256 _expires, uint256 _nonce) public {
        bytes32 orderHash = sha256(abi.encodePacked(this, _tokenGet, _amountGet, _tokenGive, _amountGive, _expires, _nonce));
        orders[msg.sender][orderHash] = 1;
    }

    function cancelOrder(address _tokenGet, uint256 _amountGet, address _tokenGive, uint256 _amountGive, uint256 _expires, uint256 _nonce, uint8 v, bytes32 r, bytes32 s) public {
        bytes32 orderHash = sha256(abi.encodePacked(this, _tokenGet, _amountGet, _tokenGive, _amountGive, _expires, _nonce));
        if(orders[msg.sender][orderHash] != 0){
            orderFills[msg.sender][orderHash] = _amountGet;
        }else{
            bytes32 hash = sha3("\x19Ethereum Signed Message:\n32", orderHash);
            address recoveredAddress = ecrecover(hash, v, r, s);
            if(recoveredAddress == msg.sender){
                orderFills[msg.sender][orderHash] = _amountGet;
            }
        }
    }

    function changeFeeMake(uint256 _feeMake) public {
        require(msg.sender == admin && _feeMake <= feemake);
        feemake = _feeMake;
    }

    function withdraw(uint256 amount) public {
        require(balanceOf[0][msg.sender] >= amount);
        balanceOf[0][msg.sender] -= amount;
        msg.sender.transfer(amount);
    }

    function changeFeeAccount(address _feeAccount) public {
        require(msg.sender == admin);
        feeaccount = _feeAccount;
    }

    function changeFeeRebate(uint256 _feerebate) public {
        require(msg.sender == admin && _feerebate >= feerebate && _feerebate <= feetake);
        feerebate = _feerebate;
    }

    function changeAdmin(address _admin) public {
        require(msg.sender == admin);
        admin = _admin;
    }

    function depositToken(address _token, uint256 _amount) public {
        require(_token!=0);
        balanceOf[msg.sender][_token] += _amount;
        require(_token.call(bytes4(bytes32(sha3("transferFrom(address,address,uint256)"))), msg.sender, this, _amount));
    }

    function changeFeeTake(uint256 _feetake) public {
        require(msg.sender == admin && _feetake >= feerebate && _feetake <= feetake);
        feetake = _feetake;
    }

    function changeAccountLevelsAddr(address _accountlevelsaddr) public {
        require(msg.sender == admin);
        accountlevelsaddr = _accountlevelsaddr;
    }

    function() external payable {
        balanceOf[0][msg.sender] += msg.value;
    }

    function withdrawToken(address _token, uint256 _amount) public {
        require(balanceOf[msg.sender][_token] >= _amount);
        balanceOf[msg.sender][_token] -= _amount;
        require(_token.call(bytes4(bytes32(sha3("transfer(address,uint256)"))), msg.sender, _amount));
    }
}
```
Please note that this contract is a simplified version of a real-world contract for the purpose of an example. It lacks important security features and should not be used in a production environment without a proper security audit.
