Here is a Solidity smart contract with the given logic. For simplicity, I've assumed that the balanceof and allowance mappings are mapping from address to uint256 and the allowance mapping is a mapping from address toa mapping from address to uint256.

```solidity
pragma solidity ^0.5.0;

contract SmartContract {
    string public standard;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    address public owner;
    address public newowner;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;

    constructor() public {
        owner = msg.sender;
    }

    function approve(address _spender, uint256 _value) public {
        require(_spender != address(0));
        allowance[msg.sender][_spender] = _value;
    }

    function() external payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function acceptOwnership() public {
        require(msg.sender == newowner);
        owner = newowner;
        newowner = address(0);
    }

    function withdraw(uint256 _value) public {
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
        msg.sender.transfer(_value);
    }

    function withdrawTokens(address _tokenContract, address _to, uint256 _value) public {
        require(_tokenContract != address(0) && _to != address(this) && msg.sender == owner && _to != address(0));
        ERC20 token = ERC20(_tokenContract);
        token.transfer(_to, _value);
    }

    function transferOwnership(address _newowner) public {
        require(msg.sender == owner && _newowner != owner);
        newowner = _newowner;
    }

    function deposit() public payable {
        balanceof[msg.sender] += msg.value;
        totalsupply += msg.value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_to != address(this) && _from != address(0) && _to != address(0));
        allowance[_from][msg.sender] -= _value;
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && _to != address(this));
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }
}

interface ERC20 {
    function transfer(address to, uint256 value) external;
}
```
