Here is a Solidity smart contract that implements the logic described:

```Solidity
pragma solidity ^0.6.2;

contract Token {
    // State variables
    uint256 public icostartdate;
    uint256 public icoenddate;
    uint256 public totalsupply;
    address payable public owner;
    bool public allowico;
    bool public locktoken;
    address payable public ethfundmain;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    mapping (address => bool) public lockstatusof;

    // Modifier to restrict functions to owner only
    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    // Constructor
    constructor() public payable {
        icostartdate = 0x5d071040;
        icoenddate = 0x5d22bfc0;
        locktoken = true;
        allowico = true;
        if (msg.value == 0) {
            owner = msg.sender;
        }
    }

    // Functions
    function resumeCrowdSale() public onlyOwner {
        allowico = true;
    }

    function haltTokenTransferFromAddress(address _address) public onlyOwner {
        lockstatusof[_address] = true;
    }

    function stopTransferToken() public onlyOwner {
        locktoken = true;
    }

    function forwardFunds() public onlyOwner {
        require(ethfundmain != address(0));
        ethfundmain.transfer(address(this).balance);
    }

    function changeReceiveWallet(address payable _newWallet) public onlyOwner {
        require(_newWallet != address(0));
        ethfundmain = _newWallet;
    }

    function haltCrowdSale() public onlyOwner {
        allowico = false;
    }

    function resumeTokenTransferFromAddress(address _address) public onlyOwner {
        lockstatusof[_address] = false;
    }

    function startTransferToken() public onlyOwner {
        locktoken = false;
    }

    function assignOwnership(address payable _newOwner) public onlyOwner {
        require(_newOwner != address(0));
        owner = _newOwner;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        require(lockstatusof[msg.sender] == false && lockstatusof[_spender] == false && locktoken == false && now > icoenddate && _spender != address(0) && _value <= balanceof[msg.sender]);
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function burn(uint256 _value) public onlyOwner returns (bool success) {
        require(_value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        if (_value <= totalsupply) {
            totalsupply -= _value;
        }
        return true;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(lockstatusof[msg.sender] == false && lockstatusof[_to] == false && locktoken == false && now > icoenddate && _to != address(0) && _value <= balanceof[msg.sender]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(lockstatusof[_from] == false && lockstatusof[_to] == false && locktoken == false && now > icoenddate && _to != address(0) && _value <= balanceof[_from] && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        return true;
    }

    function manualMint(address _to, uint256 _value) public onlyOwner returns (bool success) {
        require(_to != address(0) && _value > 0);
        uint256 amount = _value * 0xde0b6b3a7640000;
        balanceof[_to] += amount;
        totalsupply += amount;
        return true;
    }
}
```
This contract includes all the functions as described and uses the Ethereum address type for the first argument of the `haltTokenTransferFromAddress`, `changeReceiveWallet`, `assignOwnership`, `approve`, `transfer`, `transferFrom` and `manualMint` functions, and the uint256 type for values and token amounts. The `onlyOwner` modifier is used to restrict function usage to the contract owner only.
