```solidity
pragma solidity ^0.5.0;

contract MyContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public sellprice;
    uint256 public buyprice;
    mapping (address => bool) public frozenaccount;

    constructor() payable public {
        owner = msg.sender;
        decimals = 18;
    }

    function setPrices(uint256 _sellprice, uint256 _buyprice) public {
        require(msg.sender == owner);
        sellprice = _sellprice;
        buyprice = _buyprice;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function burn(uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function mintToken(address _target, uint256 _mintedAmount) public {
        require(msg.sender == owner);
        balanceof[_target] += _mintedAmount;
        totalsupply += _mintedAmount;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender] && balanceof[_from] >= _value);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender]);
        allowance[_from][msg.sender] -= _value;
        require(_to != address(0) && balanceof[_to] + _value >= balanceof[_to] && !frozenaccount[_to] && balanceof[_from] >= _value && !frozenaccount[_from]);
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function buy() payable public {
        require(!frozenaccount[msg.sender] && msg.sender != address(0) && !frozenaccount[address(this)] && balanceof[address(this)] >= msg.value*buyprice && balanceof[msg.sender] + msg.value*buyprice >= balanceof[msg.sender]);
        balanceof[address(this)] -= msg.value*buyprice;
        balanceof[msg.sender] += msg.value*buyprice;
    }

    function freezeAccount(address _target, bool _freeze) public {
        require(msg.sender == owner);
        frozenaccount[_target] = _freeze;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(_to != address(0) && balanceof[msg.sender] >= _value && balanceof[_to] + _value >= balanceof[_to] && !frozenaccount[msg.sender] && !frozenaccount[_to]);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public {
        allowance[msg.sender][_spender] = _value;
        if(_extraData.length > 0) {
            (bool success,) = _spender.call(abi.encodeWithSignature("receiveApproval(address,uint256,bytes)", msg.sender, _value, _extraData));
            require(success);
        }
    }

    function sell(uint256 _amount) public {
        require(!frozenaccount[msg.sender] && !frozenaccount[address(this)] && balanceof[address(this)] + _amount >= balanceof[address(this)] && balanceof[address(this)] >= _amount*sellprice && balanceof[msg.sender] >= _amount);
        balanceof[msg.sender] -= _amount;
        balanceof[address(this)] += _amount;
        if(sellprice > 0) {
            msg.sender.transfer(_amount/sellprice);
        }
    }
}
```
This contract includes various functionalities like buying tokens, selling tokens, freezing accounts, transferring tokens, setting prices for tokens, burning tokens from a specific account, transferring ownership of the contract, and more.
