```solidity
pragma solidity ^0.5.0;

contract YourContract {
    address public owner;
    string public name;
    string public symbol;
    uint8 public decimals = 18;
    uint256 public totalsupply;
    mapping (address => uint256) public balanceof;
    mapping (address => mapping (address => uint256)) public allowance;
    uint256 public sellprice;
    uint256 public buyprice;
    mapping (address => bool) public frozenaccount;

    constructor() payable public {
        owner = msg.sender;
    }

    function setPrices(uint256 _sellprice, uint256 _buyprice) public {
        require(msg.sender == owner);
        sellprice = _sellprice;
        buyprice = _buyprice;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function burn(uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        balanceof[msg.sender] -= _value;
        totalsupply -= _value;
    }

    function mintToken(address _target, uint256 _mintedAmount) public {
        require(msg.sender == owner);
        balanceof[_target] += _mintedAmount;
        totalsupply += _mintedAmount;
    }

    function burnFrom(address _from, uint256 _value) public {
        require(balanceof[_from] >= _value && _value <= allowance[_from][msg.sender]);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        totalsupply -= _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(_value <= allowance[_from][msg.sender] && frozenaccount[_from] == false && balanceof[_from] >= _value && _to != address(0) && balanceof[_to] + _value >= balanceof[_to] && frozenaccount[_to] == false);
        allowance[_from][msg.sender] -= _value;
        balanceof[_from] -= _value;
        balanceof[_to] += _value;
    }

    function buy() payable public {
        require(msg.sender != address(0) && frozenaccount[msg.sender] == false && buyprice != 0 && frozenaccount[address(this)] == false && balanceof[msg.sender] + msg.value / buyprice >= balanceof[msg.sender] && balanceof[address(this)] >= msg.value / buyprice);
        balanceof[address(this)] -= msg.value / buyprice;
        balanceof[msg.sender] += msg.value / buyprice;
    }

    function freezeAccount(address _target, bool _freeze) public {
        require(msg.sender == owner);
        frozenaccount[_target] = _freeze;
    }

    function transferOwnership(address _newOwner) public {
        require(msg.sender == owner);
        owner = _newOwner;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[_to] + _value >= balanceof[_to] && _to != address(0) && balanceof[msg.sender] >= _value && frozenaccount[msg.sender] == false && frozenaccount[_to] == false);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function sell(uint256 _amount) public {
        require(frozenaccount[msg.sender] == false && balanceof[address(this)] >= _amount * sellprice && balanceof[msg.sender] >= _amount && balanceof[address(this)] + _amount >= balanceof[address(this)] && frozenaccount[address(this)] == false && address(this) != address(0));
        balanceof[msg.sender] -= _amount;
        balanceof[address(this)] += _amount;
        msg.sender.transfer(_amount * sellprice);
    }

    function approveAndCall(address _spender, uint256 _value, bytes memory _extraData) public {
        allowance[msg.sender][_spender] = _value;
        ApproveAndCallFallBack(_spender).receiveApproval(msg.sender, _value, address(this), _extraData);
    }
}

contract ApproveAndCallFallBack {
    function receiveApproval(address _from, uint256 _value, address _token, bytes memory _extraData) public;
}
```
This contract includes a payable constructor, functions for setting prices, approving allowances, burning and minting tokens, burning from a specific account, transferring from one account to another, buying tokens, freezing accounts, transferring contract ownership, transferring tokens, selling tokens and approving and calling an external contract. It has the necessary checks to ensure only the owner can change important parameters, that there are enough funds for transfers and that accounts involved are not frozen.
