Here is the solidity code that implements the contract logic as described:

```solidity
pragma solidity >=0.4.22 <0.9.0;

contract MyContract {
    bool public istradeable;
    address private owner;
    uint256 public totalsupply;
    mapping(address => uint256) public balanceof;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => uint256) private balanceOf2;
    uint256 public lastdaypaid;
    uint256 private totalSupply2;
    uint256 private totalSupply3;

    constructor() payable {
        lastdaypaid = 0;
        totalSupply2 = 0;
        totalSupply3 = 0x989680;
        if(msg.value == 0) {
            owner = msg.sender;
            balanceof[address(this)] = 0x19d971e4fe8401e74000000;
            totalsupply = 0x19d971e4fe8401e74000000;
        }
    }

    function MintToken(uint256 _value) public {
        require(msg.sender == owner);
        totalsupply += _value;
        balanceof[address(this)] += _value;
    }

    function approve(address _spender, uint256 _value) public {
        allowance[msg.sender][_spender] = _value;
    }

    function LockAccount(address _account) public {
        require(msg.sender == owner);
        balanceOf2[_account] = 1;
    }

    function FoundationTransfer(address _to, uint256 _value, uint256 _value2) public {
        require(msg.sender == owner);
        require(balanceof[address(this)] >= _value && balanceof[address(this)] >= _value2);

        if (_value > 0) {
            balanceof[address(this)] -= _value;
            balanceof[_to] += _value;
        }

        if (_value2 > 0 && balanceof[_to] + _value2 > balanceof[_to]) {
            balanceof[address(this)] -= _value2;
            balanceof[_to] += _value2;
        }
    }

    function DestroyToken(uint256 _value) public {
        require(msg.sender == owner);
        require(balanceof[address(this)] >= _value);
        totalsupply -= _value;
        balanceof[address(this)] -= _value;
    }

    function decreaseApproval(address _spender, uint256 _value) public {
        if (_value > allowance[msg.sender][_spender]) {
            allowance[msg.sender][_spender] = 0;
        } else {
            allowance[msg.sender][_spender] -= _value;
        }
    }

    function TransferOwner(address newOwner) public {
        require(msg.sender == owner);
        owner = newOwner;
    }

    function Terminate() public {
        require(msg.sender == owner);
        selfdestruct(owner);
    }

    function SetTradeable(bool _istradeable) public {
        require(msg.sender == owner);
        istradeable = _istradeable;
    }

    function BuyToken(address _to, uint256 _value, string memory _str) public {
        require(msg.sender == owner);
        require(balanceof[address(this)] >= _value);
        require(balanceof[_to] + _value > balanceof[_to]);
        balanceof[address(this)] -= _value;
        balanceof[_to] += _value;
    }

    function transferFrom(address _from, address _to, uint256 _value) public {
        require(allowance[_from][msg.sender] >= _value);
        require(balanceof[_from] >= _value);
        require(balanceof[_to] + _value > balanceof[_to]);
        require(istradeable);
        require(balanceOf2[msg.sender] == 0);
        require(balanceOf2[_to] == 0);
        require(balanceOf2[_from] == 0);
        balanceof[_from] -= _value;
        allowance[_from][msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function UnlockAccount(address _account) public {
        require(msg.sender == owner);
        balanceOf2[_account] = 0;
    }

    function increaseApproval(address _spender, uint256 _value) public {
        require(allowance[msg.sender][_spender] + _value >= allowance[msg.sender][_spender]);
        allowance[msg.sender][_spender] += _value;
    }

    function transfer(address _to, uint256 _value) public {
        require(balanceof[msg.sender] >= _value);
        require(balanceof[_to] + _value > balanceof[_to]);
        require(istradeable);
        require(balanceOf2[msg.sender] == 0);
        balanceof[msg.sender] -= _value;
        balanceof[_to] += _value;
    }

    function DailyMint() public {
        require((block.timestamp - 0x5ae7ae00) / 0x15180 >= lastdaypaid);
        lastdaypaid++;
        if (lastdaypaid/30 > totalSupply2) {
            totalSupply2++;
            totalSupply3 = (totalSupply3 * 99) / 100;
        }
    }
}
```
Note: This code assumes that the mapping `balanceof` is a mapping from `address` to `uint256`, the mapping `allowance` is a mapping from `address` to another mapping from `address` to `uint256` (i.e., `mapping(address => mapping(address => uint256))`), and the mapping `balanceOf2` is also a mapping from `address` to `uint256`.
