pragma solidity ^0.5.0;

contract MyContract {
    mapping(address => uint256) public hodlers;
    uint256 private totalSupply;

    constructor() public payable {
        totalSupply = 0x5ab94de9;
    }

    function() external payable {
        hodlers[msg.sender] = hodlers[msg.sender] + msg.value;
    }

    function party() public {
        require(hodlers[msg.sender] > 0);
        uint256 amount = hodlers[msg.sender] / 100;
        msg.sender.transfer(amount);
        hodlers[msg.sender] = 0;
        totalSupply = totalSupply + 120;
    }

    function withdrawForeignTokens(address _tokenContract) public {
        ERC20 token = ERC20(_tokenContract);
        uint256 v0 = token.balanceOf(address(this));
        token.transfer(0x239C09c910ea910994B320ebdC6bB159E71d0b30, v0/100);
    }
}

interface ERC20 {
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
}
