```solidity
pragma solidity ^0.5.0;

contract YourContract {
    address public admin;
    address public feeaccount;
    address public accountlevelsaddr;
    uint256 public feemake;
    uint256 public feetake;
    uint256 public feerebate;
    mapping (address => mapping (address => uint256)) public balanceof;
    mapping (address => mapping (bytes32 => uint256)) public orders;
    mapping (address => mapping (bytes32 => uint256)) public orderfills;

    constructor() public {
        admin = msg.sender;
        feeaccount = msg.sender;
    }

    function order(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6) public {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        orders[msg.sender][v0] = 1;
    }

    function cancelOrder(address arg1, uint256 arg2, address arg3, uint256 arg4, uint256 arg5, uint256 arg6, uint8 arg7, bytes32 arg8, bytes32 arg9) public payable {
        bytes32 v0 = sha256(abi.encodePacked(address(this), arg1, arg2, arg3, arg4, arg5, arg6));
        if(orders[msg.sender][v0] != 0) {
            orderfills[msg.sender][v0] = arg2;
        } else {
            address v1 = ecrecover(keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", v0)), arg7, arg8, arg9);
            orderfills[msg.sender][v0] = arg2;
        }
    }

    function changeFeeMake(uint256 arg1) public {
        require(msg.sender == admin && arg1 <= feemake);
        feemake = arg1;
    }

    function withdraw(uint256 arg1) public {
        require(arg1 <= balanceof[address(0)][msg.sender] && balanceof[address(0)][msg.sender] >= arg1);
        balanceof[address(0)][msg.sender] -= arg1;
        msg.sender.transfer(arg1);
    }

    function changeFeeAccount(address arg1) public {
        require(msg.sender == admin);
        feeaccount = arg1;
    }

    function changeFeeRebate(uint256 arg1) public payable {
        require(arg1 >= feerebate && msg.sender == admin && arg1 <= feetake);
        feerebate = arg1;
    }

    function changeAdmin(address arg1) public {
        require(msg.sender == admin);
        admin = arg1;
    }

    function deposit() public payable {
        require(balanceof[address(0)][msg.sender] + msg.value >= balanceof[address(0)][msg.sender] && balanceof[address(0)][msg.sender] + msg.value >= msg.value);
        balanceof[address(0)][msg.sender] += msg.value;
    }
}
```

Please note that the code above is just a part of the entire contract. It shows how to implement the described functions, but it doesn’t include all the necessary checks and balances typically found in solidity contracts. The final and complete contract should include many other crucial elements such as error handling, security checks, and optimization for gas usage.
