            s_6[msg.sender] -= _value;
            require(_value + s_4[_to] >= s_4[_to]);
            s_4[_to] += _value;
        }
    }

    function transferFrom(address _from, address _to, uint256 _value) public payable {
        require(_to != address(0));
        (bool success, bytes memory data) = address(0x5491305204DaCa75317A0bC8C332dD61Dbb77Bc8).call(abi.encodePacked(bytes4(keccak256("balanceOf(address)")), _from));
        require(success);
        uint256 v0 = abi.decode(data, (uint256));
