// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

interface KittyCore {
    function transfer(address to, uint256 tokenId) external;
    function createSiringAuction(uint256 kittyId, uint256 startingPrice, uint256 endingPrice, uint256 duration) external;
}

interface KittySires {
    function cancelAuction(uint256 auctionId) external;
}

contract KittyAuction {
    address public owner;
    uint256 public auctioncreationreward;
    mapping(uint256 => uint256) public startingsiringpriceforkitty;
    uint256 public globalendingsiringprice;
    uint256 public globalauctionduration;
    address public kittycoreaddress;
    address public kittysiresaddress;
    mapping(uint256 => bool) public kittyiswhitelisted;
    uint256 public numberofwhitelistedkitties;

    constructor() payable {
        owner = msg.sender;
        auctioncreationreward = 0x2386f26fc10000;
        globalendingsiringprice = 0;
        globalauctionduration = 0x13c680;
        kittycoreaddress = 0x06012c8cf97BEaD5deAe237070F9587f8E7A266d;
        kittysiresaddress = 0xC7af99Fe5513eB6710e6D5f44F9989dA40F27F26;

        if (msg.value == 0) {
            uint256[] memory kitties = [0xcf235, 0xcf237, 0xcf238, 0xcf239, 0xcf23a, 0xcf2c6];
            for (uint i = 0; i < kitties.length; i++) {
                startingsiringpriceforkitty[kitties[i]] = 0x2c68af0bb140000;
                kittyiswhitelisted[kitties[i]] = true;
            }
            numberofwhitelistedkitties = kitties.length;
        }
    }

    function emergencyWithdraw() external {
        require(msg.sender == owner, "Only owner can call this function.");
        payable(owner).transfer(address(this).balance);
    }

    function ownerChangeGlobalEndingSiringPrice(uint256 price) external {
        require(msg.sender == owner, "Only owner can call this function.");
        globalendingsiringprice = price;
    }

    function renounceOwnership() external {
        require(msg.sender == owner, "Only owner can call this function.");
        owner = address(0);
    }

    function ownerWithdrawKitty(address to, uint256 tokenId) external {
        require(msg.sender == owner, "Only owner can call this function.");
        KittyCore(kittycoreaddress).transfer(to, tokenId);
    }

    function ownerChangeStartingSiringPrice(uint256 tokenId, uint256 price) external {
        require(msg.sender == owner, "Only owner can call this function.");
        startingsiringpriceforkitty[tokenId] = price;
    }

    function ownerChangeAuctionCreationReward(uint256 reward) external {
        require(msg.sender == owner, "Only owner can call this function.");
        auctioncreationreward = reward;
    }

    function ownerChangeGlobalAuctionDuration(uint256 duration) external {
        require(msg.sender == owner, "Only owner can call this function.");
        globalauctionduration = duration;
    }

    function ownerCancelSiringAuction(uint256 auctionId) external {
        require(msg.sender == owner, "Only owner can call this function.");
        KittySires(kittysiresaddress).cancelAuction(auctionId);
    }

    // more functions...
}
```

